#!/bin/sh
bin_path=~/.local/bin/podman
pods_dir=~/dotfiles/Pods

mkdir -p ${bin_path}

find ${pods_dir} -mindepth 1 -maxdepth 1 -type d | while read d; do
        c=`basename $d`
        echo $c;
        cat<<EOF > ${bin_path}/$c
#!/bin/sh
# this file has been autogenerated by a nasty script

podman run -ti --rm -v \$PWD:/data -v $HOME/.emacs.d:$HOME/.emacs.d/:ro localhost/$c "\$@"
EOF
        chmod +x  ${bin_path}/$c
done
