#!/bin/sh
bin_path=~/.local/bin/podman
pods_dir=~/dotfiles/Pods

mkdir -p ${bin_path}

ls ${pods_dir} | while read c; do
        echo $c;
        cat<<EOF > ${bin_path}/$c
#!/bin/sh
# this file has been autogenerated by a nasty script

podman run -ti --rm -v \$PWD:/data $c
EOF
        chmod +x  ${bin_path}/$c
done
